            yAxis : {
                show : true,
                type : 'value',
                axisLine:{
                    show:true,
                    lineStyle:{
                        color:'#fff'
                    }
                },
                axisTick:{
                    lineStyle:{
                        color:'#fff'
                    }
                },
                axisLabel:{
                    color:"#fff",
                    fontSize:16
