import React,{ Component } from 'react'
import { ChromePicker } from 'react-color'
import { Input } from 'antd'
export default class ColorPicker extends Component {
    state = {
        displayColorPicker: false
    }

    handleClick = () => {
        this.setState({ 
            displayColorPicker: !this.state.displayColorPicker 
        })
    }

    handleClose = () => {
        this.setState({ 
            displayColorPicker: false 
        })
    }

    colorPickerChange = (color,event)=>{
        this.props.onChange(this.getRgba(color))
    }

    getRgba = (color)=>{
        const { rgb } = color
        const rgbaColor = `rgba(${rgb.r},${rgb.g},${rgb.b},${rgb.a})`
        return hexify(rgbaColor)
    }

    hexify(color) {
        var values = color
            .replace(/rgba?\(/, '')
            .replace(/\)/, '')
            .replace(/[\s+]/g, '')
            .split(',');
        var a = parseFloat(values[3] || 1),
            r = Math.floor(a * parseInt(values[0]) + (1 - a) * 255),
            g = Math.floor(a * parseInt(values[1]) + (1 - a) * 255),
            b = Math.floor(a * parseInt(values[2]) + (1 - a) * 255);
        return "#" +
            ("0" + r.toString(16)).slice(-2) +
            ("0" + g.toString(16)).slice(-2) +
            ("0" + b.toString(16)).slice(-2);
    }
    render() {
        const popover = {
            position: 'absolute',
            zIndex: '2',
        }
        const cover = {
            position: 'fixed',
            top: '0px',
            right: '0px',
            bottom: '0px',
            left: '0px'
        }
        return (
            <div>
                <div className="color-picker-input">
                    <i 
                        onClick={ this.handleClick }
                        style={{background:this.props.value}}
                    ></i>
                    <Input 
                        type="text" 
                        value={this.props.value}
                        disabled={true}
                    />
                </div>
                { 
                    this.state.displayColorPicker?
                    <div style={ popover }>
                        <div style={ cover } onClick={ this.handleClose }></div>
                        <ChromePicker
                            color={ this.props.value }
                            onChangeComplete={ this.colorPickerChange }
                        />
                    </div> 
                    : null 
                }
            </div>
        )
    }
}
