import { sort_modules,change_item } from '../actions/screen-actions'
import { fromJS } from 'immutable';

const initialState = fromJS({
    id:1,
    name:"大屏数据",
    screen:{//大屏背景数据
        width:1920,
        height:1080,
        color:"rgba(0,0,0,1)",
        backgroundImg:{
            url:'bg.png',
            size:["80%","auto"],
            position:["300","40%"]
        },
        poster:"poster.png"
    },
    modules:[{
        id:"1",
        name:"基础折线图",//组件名称
        type:"line",
        style:{//折线图样式
            width:400,
            height:500,
            x:100, //组件的位置
            y:100,
            background:"rgba(0,0,0,1)",
        },
        data:{
            type:"static",
            url:"192.168.0.100",
            json:"[{aaa:111}]"
        },
        options:{
            title: {
                text: '堆叠区域图'
            },
            tooltip : {
                trigger: 'axis'
            },
            legend: {
                data:['邮件营销']
            },
            grid: {
                left: '3%',
                right: '4%',
                bottom: '3%',
                containLabel: true
            },
            xAxis : [
                {
                    type : 'category',
                    boundaryGap : false,
                    data : ['周一','周二','周三','周四','周五','周六','周日']
                }
            ],
            yAxis : [
                {
                    type : 'value'
                }
            ],
            series : [{
                    name:'邮件营销',
                    type:'line',
                    data:[120, 132, 101, 134, 90, 230, 210]
            }]
        }
    }]
})

export default (state=initialState,action)=>{
    switch (action.type){
        case sort_modules:
            return state.set("modules",fromJS(action.data))
        case change_item:
            let newMudules = state.get("modules").map(item=>{
                if(item.get("id") === action.data.id){
                    return item.mergeDeep(action.data.data)
                }else{
                    return item
                }
            })
            return state.set("modules",newMudules)
        default:
            return state
    }
}
