import React,{ Component } from 'react'
import { 
    Row,
    Col,
    Collapse,
    Form,
    Input,
    Switch 
} from 'antd'
import { connect } from 'react-redux'
import { changeItem } from '../../../store/actions/screen-actions'
import ColorPicker from '../ColorPicker'
const { Panel } = Collapse;
class SettingPropsForm extends Component{
    colorChange = (value,event)=>{
        console.log(value)
    }
    moduleStyleWidthChange = (e)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                style:{
                    width:e.target.value
                }
            }
        })
    }
    moduleStyleHeightChange = (e)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                style:{
                    height:e.target.value
                }
            }
        })
    }
    moduleStyleXChange = (e)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                style:{
                    x:e.target.value
                }
            }
        })
    }
    moduleStyleYChange = (e)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                style:{
                    y:e.target.value
                }
            }
        })
    }
    switchBackground = (checked)=>{
        const { id } = this.props.module
        if(checked){
            this.props.changeItem({
                id,
                data:{
                    style:{
                        background:"#ff0000"
                    }
                }
            })
        }else{
            this.props.changeItem({
                id,
                data:{
                    style:{
                        background:"transparent"
                    }
                }
            })
        }

    }
    moduleStyleBackgroundChange = (color)=>{
        const { id } = this.props.module
        this.props.changeItem({
            id,
            data:{
                style:{
                    background:color.hex
                }
            }
        })
    }
    render(){
        const { style,options } = this.props.module
        return (
            <div className="setting-form-wrap">
                <Form>
                    <Collapse bordered={false}>
                        <Panel header="位置大小" key="1">
                            <Row gutter={10}>
                                <Col span={6}>
                                    <label htmlFor="">大小</label>
                                </Col>
                                <Col span={9}>
                                    <Form.Item>
                                        <Input
                                            suffix="w" 
                                            value={style.width}
                                            onChange={this.moduleStyleWidthChange}
                                        />
                                    </Form.Item>
                                </Col>
                                <Col span={9}>
                                    <Form.Item>
                                        <Input 
                                            suffix="h" 
                                            value={style.height}
                                            onChange={this.moduleStyleHeightChange}
                                        />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={10}>
                                <Col span={6}>
                                    <label htmlFor="">位置</label>
                                </Col>
                                <Col span={9}>
                                    <Form.Item>
                                        <Input 
                                            suffix="x" 
                                            value={style.x}
                                            onChange={this.moduleStyleXChange}
                                        />
                                    </Form.Item>
                                </Col>
                                <Col span={9}>
                                    <Form.Item>
                                        <Input 
                                            suffix="y" 
                                            value={style.y}
                                            onChange={this.moduleStyleYChange}
                                        />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={10}>
                                <Col span={6}>
                                    <label htmlFor="">背景色</label>
                                </Col>
                                <Col span={18}>
                                    <Switch 
                                        checked={style.background!=='transparent'}
                                        style={{marginTop:5}}
                                        onChange={this.switchBackground}
                                    />
                                </Col>
                                {
                                    style.background==="transparent"?
                                    ""
                                    :
                                    <Col span={18} push={6}>
                                        <ColorPicker 
                                            value={style.background}
                                            onChange={this.moduleStyleBackgroundChange}
                                        />
                                    </Col>
                                }
                            </Row>
                        </Panel>
                        <Panel header="标题" key="2">
                            <Row gutter={10}>
                                <Col span={6}>
                                    <label htmlFor="">显示</label>
                                </Col>
                                <Col span={18}>
                                    <Switch 
                                        style={{marginTop:5}}
                                        checked={options.title.show}
                                    />
                                </Col>
                            </Row>
                        </Panel>
                        <Panel header="图例" key="3">
                            <div style={{color:"#fff"}}>图例</div>
                        </Panel>
                        <Panel header="折线图样式" key="4">
                            <div style={{color:"#fff"}}>折线图样式</div>
                        </Panel>
                        <Panel header="X轴" key="5">
                            <div style={{color:"#fff"}}>X轴</div>
                        </Panel>
                        <Panel header="Y轴" key="6">
                            <div style={{color:"#fff"}}>Y轴</div>
                        </Panel>
                        <Panel header="数据系列" key="7">
                            <div style={{color:"#fff"}}>数据系列</div>
                        </Panel>
                    </Collapse>
                </Form>
            </div>
        )
    }
}
const mapStateToProps = (state,ownProps)=>{
    return {

    }
}
const mapDispatchToProps = dispatch=>{
    return {
        changeItem(data){
            const action = changeItem(data)
            dispatch(action)
        }
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(SettingPropsForm)
