import React,{ Component,Fragment } from 'react'
import { Tabs } from 'antd'
import SettingPropsForm from './SettingPropsForm'
import SettingDataForm from './SettingDataForm'
import CanvasBgSetting from '../../Common/settings/CanvasBgSetting'
const { TabPane } = Tabs
export default class SettingFrom extends Component{
    getPropsSettingComponent = ()=>{
        if(this.props.module.id==="screen"){
            return <CanvasBgSetting/>
        }
    }
    render(){
        return (
            <Fragment>
                <Tabs className="setting-form-tab" defaultActiveKey="1">
                    <TabPane tab="属性" key="1">
                        {
                            this.props.module.id==="screen"?<CanvasBgSetting/>:<SettingPropsForm 
                                module={this.props.module}
                            />
                        }
                    </TabPane>
                    <TabPane tab="数据" key="2">
                        {
                            this.props.module.id==="screen"?<div className="setting-no-data">该组件无数据配置</div>:<SettingDataForm 
                                module={this.props.module}
                            />
                        }
                    </TabPane>
                </Tabs>
            </Fragment>
        )
    }
}
